const { createPublicClient, http, createWalletClient } = require("viem");
const { gnosis } = require("viem/chains");
const { privateKeyToAccount, generatePrivateKey, privateKeyToAddress } = require("viem/accounts");
const privateKey = generatePrivateKey();

const client = createWalletClient({
  chain: gnosis,
  transport: http(),
});


// validator  0x1362fd98414FA56b3638679756337186d6d41E5F
// private key  64299807903803580821972963416651137686319442845143049914343728309757645799598
const account = privateKeyToAccount(
  privateKey
);
const main = async () => {
  console.log(privateKey);
  console.log(privateKeyToAddress(privateKey));
  // private key = 0x61e582d48958d1d0a9187786f91477ce3ae9b76f60d2edee2b9e6e9f8924978a
  // address = 0xb337aB0e027083E9a6026D46AFF5FA07FffD9E00
  const messageData = "";
  const signature = await client.signMessage({
    account,
    message: { raw: messageData }
  })
  console.log("signature: ", signature);


}

main()